import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { useCooperation } from '../../context/CooperationContext'; // Импортираме контекста

const CooperationTemplate = () => {
    const { id } = useParams(); // Вземаме ID-то на кооперацията от URL-то
    const [cooperationData, setCooperationData] = useState(null);
    const [error, setError] = useState(null);
    const { setCurrentCooperation } = useCooperation(); // Вземаме функцията за задаване на глобалното състояние

    useEffect(() => {
        const fetchCooperationData = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/buildings/${id}`);
                const cooperation = response.data;

                setCooperationData(cooperation);
                setCurrentCooperation(cooperation); // Задаваме новата кооперация в глобалното състояние
            } catch (error) {
                console.error('Error fetching cooperation data:', error);
                setError('Error fetching data');
            }
        };

        fetchCooperationData();
    }, [id, setCurrentCooperation]);

    if (error) {
        return <div>{error}</div>; // Показваме съобщение за грешка
    }

    if (!cooperationData) {
        return <div>Loading...</div>; // Показваме съобщение за зареждане
    }

    return (
        <div>
            <h1>{cooperationData.title}</h1>
            {/* Тук можете да добавите повече съдържание свързано с кооперацията */}
        </div>
    );
};

export default CooperationTemplate;
