import React from 'react';
import { useNavigate } from 'react-router-dom';

const FloorComponent = ({ floor, projectTitle }) => {
    const navigate = useNavigate();

    const handleViewDetails = () => {
        navigate(`/floor/information/${floor.id}`, { state: { projectTitle } });
    };

    const handleAddInformation = () => {
        // Логика за добавяне на апартамент
        alert(`Adding apartment with ID ${floor.id}`);
    };

    return (
        <div className="project-card" style={{ cursor: 'pointer', position: 'relative', display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '10px', border: '1px solid #ccc', borderRadius: '5px', marginBottom: '10px' }}>
            <div className="project-info" onClick={handleViewDetails} style={{ display: 'flex', flexDirection: 'column' }}>
                <h2 style={{ margin: 0 }}>Floor Nº {floor.number}</h2>
                <p style={{ margin: '5px 0' }}><strong>Area: </strong> {floor.area} m²</p>
            </div>

            <div 
                className="add-apartment-button" 
                onClick={handleAddInformation}
                style={{ cursor: 'pointer', padding: '5px', backgroundColor: '#007bff', borderRadius: '50%', display: 'flex', alignItems: 'center', justifyContent: 'center' }}
            >
                <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="white" className="bi bi-plus" viewBox="0 0 16 16">
                    <path d="M8 4a.5.5 0 0 1 .5.5v3h3a.5.5 0 0 1 0 1h-3v3a.5.5 0 0 1-1 0v-3h-3a.5.5 0 0 1 0-1h3v-3A.5.5 0 0 1 8 4"/>
                </svg>
            </div>
        </div>
    );
};

export default FloorComponent;
