import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './CreateSell.css';
import axios from 'axios';
import PaymentContent from './PaymentContent';

const CreateSell = () => {
    const { id } = useParams();

    const [formData, setFormData] = useState({
        id: id,
        paymentSchemaId: '',
        installmentDates: '', // Това поле вече няма да се ползва за вноски, но го запазваме
        installments: [] // Добавяме ново поле за масива от вноски
    });

    const [paymentSchemas, setPaymentSchemas] = useState([]);
    const [success, setSuccess] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const paymentSchemasResponse = await axios.get('http://localhost:8080/payment-schema');
                setPaymentSchemas(paymentSchemasResponse.data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        fetchData();
    }, [id]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    // Ново: Функция за актуализиране на вноските в масива installments
    const handleInstallmentsChange = (index, field, value) => {
        const newInstallments = [...formData.installments];
        // Проверка за съществуващ обект за съответния индекс
        if (!newInstallments[index]) {
            newInstallments[index] = { installment: '', sumInEuros: '', date: null };
        }
        // Обновяване на полето във вноската
        newInstallments[index][field] = value;

        setFormData({
            ...formData,
            installments: newInstallments
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Form Data:', formData);
    };

    const createSell = async () => {
        try {
            // Преобразуване на вноските в подходящ формат за изпращане към бекенда
            const dataToSend = {
                ...formData,
                installments: formData.installments.map(installment => ({
                    installment: installment.installment,
                    sumInEuros: parseFloat(installment.sumInEuros),
                    date: installment.date ? installment.date.toISOString().split('T')[0] : null
                }))
            };

            const response = await axios.post(
                `http://localhost:8080/cooperation/${id}/create-sell`,
                dataToSend,
                {
                    headers: { 'Content-Type': 'application/json' }
                }
            );

            console.log('POST request successful', response.data);
            setSuccess('Sell created successfully!');
        } catch (error) {
            console.error('Error creating sell', error);
            setSuccess(null);
        }
    };

    return (
        <div className="create-sell-container">
            <h1>Create Sell</h1>
            <form onSubmit={handleSubmit}>
                <PaymentContent
                    formData={formData}
                    handleInputChange={handleInputChange}
                    paymentSchemas={paymentSchemas}
                    success={success}
                    handleInstallmentsChange={handleInstallmentsChange} // Предаваме новата функция на PaymentContent
                />
                <button type="submit" className="btn btn-outline-primary mt-3" onClick={createSell}>
                    Create Sell
                </button>
            </form>
        </div>
    );
};

export default CreateSell;
