import React, { useState } from 'react';
import './CreatePaymentSchema.css';

const CreatePaymentSchema = () => {
    const [formData, setFormData] = useState({
        count: ''
    });
    
    const [errors, setErrors] = useState({});
    const [success, setSuccess] = useState(null);

    const handleInputChange = (e) => {
        const { name, value } = e.target;

        // Ограничаване на стойността до 10
        if (name === 'count' && value > 10) {
            setErrors({
                ...errors,
                count: 'Installments count cannot be more than 10.'
            });
        } else {
            setErrors({
                ...errors,
                count: null
            });
        }

        // Ъпдейт на стойността
        setFormData({
            ...formData,
            [name]: value
        });
    };

    return (
        <div className="card mt-4">
            <h1 className='schema-title'>Create Payment Schema</h1>
            <form>
                {success && <div className="alert alert-success">Schema created successfully!</div>}

                <div className="form-group-count">
                    <label htmlFor="count">Count Installments</label>
                    <input
                        type="number"
                        name="count"
                        className={`form-control no-spinner ${errors.count ? 'is-invalid' : ''}`}
                        value={formData.count}
                        onChange={handleInputChange}
                        placeholder="Count"
                        min="1"
                        max="10" // Добавя стрелки и ограничава ръчното въвеждане
                    />
                    {errors.count && <div className="invalid-feedback">{errors.count}</div>}
                </div>

                <button type="submit" className="btn btn-outline-primary mt-5 schema">
                    Create Schema                
                </button> 
            </form>
        </div>
    );
};

export default CreatePaymentSchema;
