import React, { useState } from 'react';
import axios from 'axios';
import './UpdateApartment.css'; // Добави стилизация за модала

const UpdateApartment = ({ apartmentId, onClose }) => {
    const [info, setInfo] = useState({
        field1: '',
        field2: '',
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setInfo({
            ...info,
            [name]: value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`http://localhost:8080/apartment/add-information/${apartmentId}`, info);
            alert('Information updated successfully!');
            onClose(); // Затваряме модала след успешно обновяване
        } catch (error) {
            console.error('Error updating apartment:', error);
            alert('Failed to update apartment information.');
        }
    };

    return (
        <div className="modal-backdrop">
            <div className="modal-content">
                <h2>Add Information: Апартамент {apartmentId}</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="field1">Поле 1</label>
                        <input
                            type="text"
                            id="field1"
                            name="field1"
                            value={info.field1}
                            onChange={handleInputChange}
                            required
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="field2">Поле 2</label>
                        <input
                            type="text"
                            id="field2"
                            name="field2"
                            value={info.field2}
                            onChange={handleInputChange}
                            required
                        />
                    </div>

                    <div className="modal-actions">
                        <button type="submit" className="btn btn-primary">Add Information</button>
                        <button type="button" className="btn btn-secondary" onClick={onClose}>Cancel</button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default UpdateApartment;
