import React, { useState } from 'react';
import './AllApartmentsForSaleModal.css';

const AllApartmentsForSaleModal = ({ show, handleClose, apartments }) => {
    const [showRadios, setShowRadios] = useState(false);

    if (!show) return null;

    const handleCheckboxToggle = () => {
        setShowRadios(!showRadios);
    };

    return (
        <div className="modal-overlay">
            <div className="modal-content">
                {/* Fixed header with title and close button */}
                <div className="modal-header">
                    <h2>Select Apartment</h2>

                    <div className="form-check form-switch">
                        <input
                            className="form-check-input"
                            type="checkbox"
                            role="switch"
                            id="flexSwitchCheckDefault"
                            onChange={handleCheckboxToggle}
                        />
                        <label htmlFor="flexSwitchCheckDefault">Select</label>
                    </div>
                </div>
                
                {/* Scrollable list of apartments */}
                <div className="apartments-list">
                    {apartments.length > 0 ? (
                        apartments.map((apartment) => (
                            <div key={apartment.id} className="apartment-item">
                                {showRadios && (
                                    <input
                                        className="form-check-input"
                                        type="radio"
                                        name="selectedApartment"
                                        id={`apartment-${apartment.id}`}
                                        value={apartment.number}
                                    />
                                )}
                                <label htmlFor={`apartment-${apartment.id}`} className="apartment-number">
                                    {apartment.number}
                                </label>
                                <span className="apartment-price">{apartment.priceEur.toFixed(2)} â‚¬</span>
                            </div>
                        ))
                    ) : (
                        <p>No apartments available</p>
                    )}
                </div>
                <button onClick={handleClose} className="close-button">Close</button>
            </div>
        </div>
    );
};

export default AllApartmentsForSaleModal;
