import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './CreateSell.css';
import axios from 'axios';
import ContractContent from './ContractContent';

const CreateSell = () => {
    const { id } = useParams();
    const [selectedFile, setSelectedFile] = useState(null);
    const [success, setSuccess] = useState(null);

    const [formData, setFormData] = useState({
        id: id,
    });

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        setSelectedFile(file);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        createSell(); // Извиква функцията за създаване на заявка
    };

    const createSell = async () => {
        const dataToSend = new FormData();
    
        // Добавяме файла към заявката
        dataToSend.append("file", selectedFile);
    
        // Добавяме JSON данни
        const jsonObject = {
            name: "Example Name",
            description: "Example description",
            price: 123.45
        };
        dataToSend.append("data", JSON.stringify(jsonObject));
    
        try {
            const response = await axios.post(
                `http://localhost:8080/cooperation/create-sell/${id}`,
                dataToSend,
                {
                    headers: { 'Content-Type': 'multipart/form-data' }
                }
            );
    
            console.log('POST request successful', response.data);
            setSuccess('Sell created successfully!');
        } catch (error) {
            console.error('Error creating sell', error);
            setSuccess(null);
        }
    };
    

    return (
        <div className="create-sell-container">
            <h1>Create Sell</h1>
            <form onSubmit={handleSubmit}>
                <ContractContent
                    formData={formData}
                    handleInputChange={handleInputChange}
                    handleFileClick={() => document.getElementById('fileInput').click()}
                    handleFileChange={handleFileChange}
                    success={success}
                />
                <button type="submit" className="btn btn-outline-primary mt-3">
                    Create Sell
                </button>
            </form>
        </div>
    );
};

export default CreateSell;
