const CreateSell = () => {
    const { id } = useParams();
    const [showModal, setShowModal] = useState(false); // State to control modal visibility
    const [selectedApartments, setSelectedApartments] = useState([]); // State to store selected apartments (whole objects)

    const [formData, setFormData] = useState({
        id: id,
        contractNumber: '',
        purchaserFirstName: '',
        purchaserLastName: '',
        discount: '',
        brokerFirstName: '',
        brokerLastName: '',
        brokerProfit: '',
        entrance: '',
        floor: '',
        apartment: '',
        undergroundFloor: '',
        garage: '',
        parkingPlace: '',
        paymentSchemaId: '',
        installments: '',
        installmentDates: ''
    });

    const [paymentSchemas, setPaymentSchemas] = useState([]); 
    const [apartments, setApartments] = useState([]);
    const [garages, setGarages] = useState([]);
    const [parkingPlaces, setParkingPlaces] = useState([]);
    const [success, setSuccess] = useState(null);

    const handleApartmentSelect = (apartment) => {
        // Добавяне на целия обект на апартамента в масива, без да заменяме предишните
        setSelectedApartments((prevSelected) => [...prevSelected, apartment]);
    };

    useEffect(() => {
        const fetchData = async () => {
            try {
                const paymentSchemasResponse = await axios.get('http://localhost:8080/payment-schema');
                setPaymentSchemas(paymentSchemasResponse.data);

                const apartmentsResponse = await axios.get(`http://localhost:8080/apartments/${id}`);
                setApartments(apartmentsResponse.data);

                const garagesResponse = await axios.get(`http://localhost:8080/garages/${id}`);
                setGarages(garagesResponse.data);

                const parkingPlacesResponse = await axios.get(`http://localhost:8080/parking-places/${id}`);
                setParkingPlaces(parkingPlacesResponse.data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        fetchData();
    }, [id]);

    const handleFileChange = (e) => {
        const filePath = e.target.files[0].name;
        console.log('Selected file path:', filePath);
    };

    const handleFileClick = () => {
        document.getElementById('fileInput').click();
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Form Data:', formData);
    };

    const handleOpenModal = () => {
        setShowModal(true);
    };

    const handleCloseModal = () => {
        setShowModal(false);
    };

    return (
        <div className="create-sell-container">
            <h1>Create Sell</h1>
            <form onSubmit={handleSubmit}>
                <ContractContent 
                    formData={formData}
                    handleInputChange={handleInputChange}
                    handleFileClick={handleFileClick}
                    handleFileChange={handleFileChange}
                    success={success}
                />

                <ObjectsPriceContent 
                    formData={formData}
                    handleInputChange={handleInputChange}
                    success={success}
                    apartments={apartments} 
                    garages={garages} 
                    parkingPlaces={parkingPlaces} 
                    onApartmentClick={handleOpenModal} // Pass the function to open the modal
                />

                <PaymentContent
                    formData={formData}
                    handleInputChange={handleInputChange}
                    paymentSchemas={paymentSchemas}
                    success={success}
                />

                <button type="submit" className="btn btn-outline-primary mt-3">
                    Create Sell
                </button>
            </form>

            {/* Modal Component */}
            <AllApartmentsForSaleModal 
                show={showModal} 
                handleClose={handleCloseModal} 
                apartments={apartments} 
                onApartmentSelect={handleApartmentSelect} // Pass select function to modal
            />

            {/* Display selected apartments */}
            {selectedApartments.length > 0 && (
                <div className="selected-apartments mt-3">
                    <h5>Selected Apartments:</h5>
                    <ul>
                        {/* Показваме само номера на апартамента, въпреки че съхраняваме целия обект */}
                        {selectedApartments.map((apartment, index) => (
                            <li key={index}>{apartment.number}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default CreateSell;
