import React, { useState } from 'react';
import axios from 'axios';
import './UpdateCooperation.css'; // Добавяме CSS стилове

const UpdateCooperation = ({ show, handleClose, cooperationId, refreshCooperation }) => {
    const [rsp, setRsp] = useState(''); // Връщаме състоянието за rsp
    const [description, setDescription] = useState(''); // Добавяме състояние за описанието

    const handleSaveChanges = async () => {
        const updateData = {
            cooperationId: cooperationId,
            description: description, // Добавяме описанието
            rsp: rsp // Добавяме РЗП
        };

        try {
            const response = await axios.put(`http://localhost:8080/buildings/update/cooperation`, updateData);
            console.log(response.data);
            handleClose(); // Затваряне на модала след успешния запис
            refreshCooperation(); // Презареждаме списъка с апартаменти
        } catch (error) {
            console.error("Error updating cooperation:", error);
        }
    };

    return (
        <div className={`modal ${show ? 'show' : ''}`} 
             style={{ display: show ? 'block' : 'none', backgroundColor: 'rgba(0, 0, 0, 0.5)' }} 
             tabIndex="-1" 
             role="dialog">
            <div className="modal-dialog modal-dialog-centered" role="document">
                <div className="modal-content">
                    <div className="modal-header">
                        <h5 className="modal-title">Update Block</h5>
                    </div>
                    <div className="modal-body">
                        <div className="form-group">
                            <label htmlFor="description">Description</label>
                            <textarea
                                className="form-control"
                                id="description"
                                rows="5" // Определяме броя на редовете, за да направим полето по-издължено
                                value={description}
                                onChange={(e) => setDescription(e.target.value)}
                                placeholder="Information"
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="rsp">RSP</label>
                            <input
                                type="number"
                                className="form-control no-arrows" // Добавяме клас за премахване на стрелките
                                id="rsp"
                                value={rsp}
                                onChange={(e) => setRsp(e.target.value)}
                                placeholder="m²"
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="stages">Stages</label>
                            <select className="form-control" id="stages">
                                <option value="">Select Stage</option>
                                {/* Добавете логика за етапи тук */}
                            </select>
                        </div>
                    </div>
                    <div className="modal-footer">
                        <button type="button" className="btn btn-primary" onClick={handleSaveChanges}>Save changes</button>
                        <button type="button" className="btn btn-secondary" onClick={handleClose}>Close</button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default UpdateCooperation;
