import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const CreateProject = () => {
  const [formData, setFormData] = useState({
    title: '',
    startDate: new Date(),
    floors: '',
    apartments: '',
    garages: ''
  });

  const [errors, setErrors] = useState({});

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleDateChange = (date) => {
    setFormData({
      ...formData,
      startDate: date
    });
  };

  const createBuilding = (e) => {
    e.preventDefault();
    // Валидация на полетата
    const newErrors = {};
    if (!formData.title) newErrors.title = 'Title is required';
    if (!formData.floors) newErrors.floors = 'Number of floors is required';
    if (!formData.apartments) newErrors.apartments = 'Number of apartments is required';
    if (!formData.garages) newErrors.garages = 'Number of garages is required';

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
    } else {
      // Тук може да добавиш логика за създаване на проекта
      console.log('Building Created:', formData);
      alert('Building created successfully!');
    }
  };

  return (
    <div className="col-md-4 mb-4">
      <div className="card">
        <div className="card-body">
          <h1>Create a Building</h1>
          <form onSubmit={createBuilding} className="mt-4">
            <div className="form-group">
              <label htmlFor="title" className="form-label">Title</label>
              <input
                type="text"
                className={`form-control ${errors.title ? 'is-invalid' : ''}`}
                id="title"
                name="title"
                placeholder="Title"
                value={formData.title}
                onChange={handleInputChange}
                required
              />
              {errors.title && <small className="text-danger">{errors.title}</small>}
            </div>

            <div className="form-group">
              <label htmlFor="eik" className="form-label">Eik</label>
              <input
                type="text"
                className={`form-control ${errors.title ? 'is-invalid' : ''}`}
                id="title"
                name="title"
                placeholder="E"
                value={formData.title}
                onChange={handleInputChange}
                required
              />
              {errors.title && <small className="text-danger">{errors.title}</small>}
            </div>

            <div className="form-group">
              <label htmlFor="startDate" className="form-label">Start Date</label>
              <DatePicker
                selected={formData.startDate}
                onChange={handleDateChange}
                dateFormat="dd/MM/yyyy"
                className="form-control"
                calendarClassName="calendar"
              />
            </div>

            <div className="form-group">
              <label htmlFor="startDate" className="form-label">End Date</label>
              <DatePicker
                selected={formData.startDate}
                onChange={handleDateChange}
                dateFormat="dd/MM/yyyy"
                className="form-control"
                calendarClassName="calendar"
              />
            </div>

            

            

            <button type="submit" className="btn btn-outline-primary mt-3">
              Create Building
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default CreateProject;
