import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const IndividualProjectTemplate = () => {
    const { id } = useParams(); // Взимаме ID на проекта от URL параметрите
    const [project, setProject] = useState(null); // Данни за проекта
    const [buildings, setBuildings] = useState([]); // Съхраняваме данни за сградите
    const [error, setError] = useState(null); // Съхраняваме евентуални грешки

    useEffect(() => {
        const fetchProject = async () => {
            try {
                // GET заявка за конкретния проект по ID
                const response = await fetch(`http://localhost:8080/projects/${id}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch project data');
                }
                const data = await response.json();
                setProject(data); // Запазваме данните за проекта в state

                // Вземаме parcelId от респонса на проекта
                const parcelId = data.parcelId;

                // GET заявка за сградите по parcelId
                const buildingsResponse = await fetch(`http://localhost:8080/buildings/allByParcel/${parcelId}`);
                if (!buildingsResponse.ok) {
                    throw new Error('Failed to fetch buildings data');
                }
                const buildingsData = await buildingsResponse.json();
                setBuildings(buildingsData); // Запазваме данните за сградите в state
            } catch (error) {
                console.error('Error fetching project or buildings:', error);
                setError('Error fetching data'); // Запазваме грешка в state
            }
        };

        fetchProject();
    }, [id]);

    // Ако има грешка при заявките, показваме съобщение за грешка
    if (error) {
        return <p>{error}</p>;
    }

    // Ако данните за проекта все още не са заредени, показваме "Loading..."
    if (!project) {
        return <p>Loading project...</p>;
    }

    return (
        <div>
            <h1>Project: {project.title}</h1> {/* Визуализиране на заглавието на проекта */}

            <h2>Buildings:</h2>
            {/* Проверка дали има налични сгради */}
            {buildings.length > 0 ? (
                <ul>
                    {buildings.map((building) => (
                        <li key={building.id}>{building.title}</li> // Визуализиране на заглавието на всяка сграда
                    ))}
                </ul>
            ) : (
                <p>No buildings found for this parcel.</p> // Ако няма сгради
            )}
        </div>
    );
};

export default IndividualProjectTemplate;
