// src/apartment/AllApartments.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import ApartmentComponent from './ApartmentComponent'; // Импортираме компонента
import './Apartment.css'; // Импортираме новия CSS файл

const AllApartments = () => {
    const { id } = useParams(); // ID-то на кооперацията от URL-то
    const [apartments, setApartments] = useState([]); // Държим апартаментите
    const [error, setError] = useState(null); // Държим грешките, ако има

    useEffect(() => {
        const fetchApartments = async () => {
            console.log(`Fetching apartments for cooperation ${id}`);
            try {
                const response = await axios.get(`http://localhost:8080/apartments/${id}`);
                console.log(response); // Вижте какъв е отговорът от API-то
                setApartments(response.data);
            } catch (err) {
                console.error(err); // Принтира грешката в конзолата
                setError('Failed to fetch apartments.');
            }
        };

        fetchApartments();
    }, [id]);

    return (
        <div>
            <h1>Apartments in Cooperation {id}</h1>
            {error && <p className="text-danger">{error}</p>}
            <div className="apartment-container">
                {apartments.map(apartment => (
                    <ApartmentComponent
                        key={apartment.id}
                        apartment={apartment}
                        projectTitle={`Cooperation ${id}`} // Ако имаш нужда от projectTitle
                    />
                ))}
            </div>
        </div>
    );
};

export default AllApartments;
