import React, { useState } from 'react';

import axios from 'axios';

const UpdateCooperation = ({ show, handleClose,  cooperationId, refreshCooperation }) => {
    const [area, setArea] = useState('');
    const [bedroomCount, setBedroomCount] = useState('');
    const [bathroomCount, setBathroomCount] = useState('');
    const [price, setPrice] = useState('');
    const [floorId, setFloorId] = useState('');

    const handleSaveChanges = async () => {
        const updateData = {
    
            cooperationId: cooperationId, 
            
        
        };
        
        try {
            const response = await axios.put(`http://localhost:8080/update/cooperation`, updateData);
            console.log(response.data);
            handleClose(); // Затваряне на модала след успешния запис
            refreshCooperation(); // Презареждаме списъка с апартаменти
        } catch (error) {
            console.error("Error updating cooperation:", error);
        }
    };

    return (
        <div className={`modal ${show ? 'show' : ''}`} 
             style={{ display: show ? 'block' : 'none', backgroundColor: 'rgba(0, 0, 0, 0.5)' }} 
             tabIndex="-1" 
             role="dialog">
            <div className="modal-dialog modal-dialog-centered" role="document">
                <div className="modal-content">
                    <div className="modal-header">
                        <h5 className="modal-title">Update Block</h5>
                    </div>
                    <div className="modal-body">
                        <div className="form-group">
                            <label htmlFor="area">RSP</label>
                            <input
                                type="number"
                                className="form-control"
                                id="area"
                                value={area}
                                onChange={(e) => setArea(e.target.value)}
                                placeholder="m²"/>
                        </div>
                        <div className="form-group">
                            <label htmlFor="floor">Srages</label>
                            <select className="form-control" id="floor" value={floorId} onChange={(e) => setFloorId(e.target.value)}>
                                <option value="">Select Stage</option>
                                {}
                            </select>
                        </div>
                       
                        
                        
                    </div>
                    <div className="modal-footer">
                        <button type="button" className="btn btn-primary" onClick={handleSaveChanges}>Save changes</button>
                        <button type="button" className="btn btn-secondary" onClick={handleClose}>Close</button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default UpdateCooperation;
