import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import ApartmentComponent from './ApartmentComponent';
import UpdateApartment from './UpdateApartment'; // Импортираме UpdateApartment компонента
import './Apartment.css';

const AllApartments = () => {
    const { id } = useParams();
    const [apartments, setApartments] = useState([]);
    const [selectedApartment, setSelectedApartment] = useState(null); // За избрания апартамент
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchApartments = async () => {
            console.log(`Fetching apartments for cooperation ${id}`);
            try {
                const response = await axios.get(`http://localhost:8080/apartments/${id}`);
                console.log(response);
                setApartments(response.data);
            } catch (err) {
                console.error(err);
                setError('Failed to fetch apartments.');
            }
        };

        fetchApartments();
    }, [id]);

    // Функция за показване на модала за конкретен апартамент
    const handleAddApartment = (apartment) => {
        setSelectedApartment(apartment); // Запазваме избрания апартамент
    };

    // Функция за затваряне на модала
    const handleCloseModal = () => {
        setSelectedApartment(null); // Затваряме модала като изчистим избрания апартамент
    };

    return (
        <div>
            <h1>Apartments in Cooperation {id}</h1>
            {error && <p className="text-danger">{error}</p>}
            <div className="apartment-container">
                {apartments.map(apartment => (
                    <ApartmentComponent
                        key={apartment.id}
                        apartment={apartment}
                        projectTitle={`Cooperation ${id}`}
                        onAddApartment={() => handleAddApartment(apartment)} // Добавяме функцията за модала
                    />
                ))}
            </div>

            {/* Показваме модала ако има избран апартамент */}
            {selectedApartment && (
                <UpdateApartment
                    show={!!selectedApartment}
                    handleClose={handleCloseModal}
                    apartmentNumber={selectedApartment.number}
                    cooperationNumber={id}
                    floors={[{ id: 1, number: 1 }, { id: 2, number: 2 }]} // Примерни етажи
                    apartmentId={selectedApartment.id}
                />
            )}
        </div>
    );
};

export default AllApartments;
