import React, { useState, useEffect } from 'react';
import { useCooperation } from '../../context/CooperationContext'; // Импортираме контекста
import axios from 'axios';
import CooperationNavbar from '../../navigation/CooperationNavbar';

const CooperationTemplate = () => {
    const { currentCooperation } = useCooperation(); // Вземаме глобалното състояние на кооперацията
    const [formData, setFormData] = useState({
        id: currentCooperation?.id || '',  // Задаваме ID-то на кооперацията от контекста
        entrance: '',
        floor: '',
        apartment: '',
        undergroundFloor: '',
        garage: '',
        parkingPlaces: ''
    });
    const [errors, setErrors] = useState({});
    const [success, setSuccess] = useState(null);

    useEffect(() => {
        if (currentCooperation) {
            // Когато контекста се промени, актуализираме ID-то в данните на формата
            setFormData((prevData) => ({
                ...prevData,
                id: currentCooperation.id
            }));
        }
    }, [currentCooperation]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const validateForm = () => {
        const newErrors = {};
        if (!formData.entrance) newErrors.entrance = 'Entrance Count is required';
        if (!formData.floor) newErrors.floor = 'Floor Count is required';
        if (!formData.apartment) newErrors.apartment = 'Apartment Count is required';
        if (!formData.undergroundFloor) newErrors.undergroundFloor = 'Underground Floor Count is required';
        if (!formData.garage) newErrors.garage = 'Garage Count is required';
        if (!formData.parkingPlaces) newErrors.parkingPlaces = 'Parking Place Count is required';

        return newErrors;
    };

    const createBuilding = async (e) => {
        e.preventDefault();

        const newErrors = validateForm();
        if (Object.keys(newErrors).length > 0) {
            setErrors(newErrors);
        } else {
            try {
                const response = await axios.post('http://localhost:8080/quick-create', formData);
                console.log('Building created successfully:', response.data);
                setSuccess('Building created successfully!');
                setErrors({});

                setFormData({
                    id: currentCooperation.id,
                    entrance: '',
                    floor: '',
                    apartment: '',
                    undergroundFloor: '',
                    garage: '',
                    parkingPlaces: ''
                });
            } catch (error) {
                console.error('Error creating building:', error);
                setSuccess(null);
            }
        }
    };

    if (!currentCooperation) {
        return <div>Loading...</div>; // Ако няма заредена кооперация, показваме съобщение за зареждане
    }

    return (
        <div>
            <CooperationNavbar /> {/* Navbar за кооперацията */}

            <h1>Cooperation: {currentCooperation.title}</h1>
            <div className="buildings-container">
                <div>Description: {currentCooperation.description}</div>
                <div>Rsp: {currentCooperation.rsp}</div>
                <div>Stages: AKT 14, AKT 15</div>
            </div>

            {/* Форма за създаване на нова сграда в кооперацията */}
            <form onSubmit={createBuilding}>
                <div>
                    <label>Entrance Count</label>
                    <input
                        type="text"
                        name="entrance"
                        value={formData.entrance}
                        onChange={handleInputChange}
                    />
                    {errors.entrance && <div style={{ color: 'red' }}>{errors.entrance}</div>}
                </div>

                <div>
                    <label>Floor Count</label>
                    <input
                        type="text"
                        name="floor"
                        value={formData.floor}
                        onChange={handleInputChange}
                    />
                    {errors.floor && <div style={{ color: 'red' }}>{errors.floor}</div>}
                </div>

                <div>
                    <label>Apartment Count</label>
                    <input
                        type="text"
                        name="apartment"
                        value={formData.apartment}
                        onChange={handleInputChange}
                    />
                    {errors.apartment && <div style={{ color: 'red' }}>{errors.apartment}</div>}
                </div>

                <div>
                    <label>Underground Floor Count</label>
                    <input
                        type="text"
                        name="undergroundFloor"
                        value={formData.undergroundFloor}
                        onChange={handleInputChange}
                    />
                    {errors.undergroundFloor && (
                        <div style={{ color: 'red' }}>{errors.undergroundFloor}</div>
                    )}
                </div>

                <div>
                    <label>Garage Count</label>
                    <input
                        type="text"
                        name="garage"
                        value={formData.garage}
                        onChange={handleInputChange}
                    />
                    {errors.garage && <div style={{ color: 'red' }}>{errors.garage}</div>}
                </div>

                <div>
                    <label>Parking Places Count</label>
                    <input
                        type="text"
                        name="parkingPlaces"
                        value={formData.parkingPlaces}
                        onChange={handleInputChange}
                    />
                    {errors.parkingPlaces && (
                        <div style={{ color: 'red' }}>{errors.parkingPlaces}</div>
                    )}
                </div>

                <button type="submit">Create Building</button>
                {success && <div style={{ color: 'green' }}>{success}</div>}
            </form>
        </div>
    );
};

export default CooperationTemplate;
