import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const AllApartments = () => {
    const { id } = useParams(); // ID-то на кооперацията от URL-то
    const [apartments, setApartments] = useState([]); // Държим апартаментите
    const [error, setError] = useState(null); // Държим грешките, ако има

    useEffect(() => {
        // Изпращаме GET заявка към API-то за апартаментите на конкретната кооперация
        const fetchApartments = async () => {
            try {
                const response = await axios.get(`http://localhost:3000/cooperation/${id}/apartments`);
                setApartments(response.data);
            } catch (err) {
                setError('Failed to fetch apartments.');
            }
        };

        fetchApartments();
    }, [id]); // Заявката се изпълнява всеки път, когато ID-то на кооперацията се промени

    return (
        <div>
            <h1>Apartments for Cooperation {id}</h1>
            {error && <p className="text-danger">{error}</p>}
            <ul>
                {apartments.map(apartment => (
                    <li key={apartment.id}>
                        Apartment #{apartment.number}, Floor: {apartment.floor}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default AllApartments;
