import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // Импорт на useParams за получаване на параметъра от URL

const IndividualProjectTemplate = () => {
    const { id } = useParams(); // Взимаме ID на проекта от URL параметрите
    const [project, setProject] = useState(null);

    useEffect(() => {
        const fetchProject = async () => {
            try {
                const response = await fetch(`http://localhost:8080/projects/${id}`); // GET заявка за конкретния проект
                const data = await response.json();
                setProject(data); // Запазваме данните за проекта в state
            } catch (error) {
                console.error('Error fetching project:', error);
            }
        };

        fetchProject();
    }, [id]);

    if (!project) {
        return <p>Loading project...</p>;
    }

    return (
        <div>
            <h1>Project: {project.title}</h1> {/* Визуализиране на заглавието на проекта */}
            {/* Тук можеш да добавиш и други полета за проекта */}
            //
        </div>
    );
};

export default IndividualProjectTemplate;
