import React, { useState } from 'react';
import './CreatePaymentSchema.css';

const CreatePaymentSchema = () => {
    const [formData, setFormData] = useState({
        count: ''
    });
    
    const [errors, setErrors] = useState({});
    const [success, setSuccess] = useState(null);

    const handleInputChange = (e) => {
        const { name, value } = e.target;

        // Валидация на полето за брой вноски
        let newErrors = { ...errors };
        if (isNaN(value)) {
            newErrors.count = 'Value must be a number.';
        } else if (value > 10) {
            newErrors.count = 'Installments count cannot be more than 10.';
        } else {
            newErrors.count = null;
        }

        // Ъпдейт на стойността и грешките
        setFormData({
            ...formData,
            [name]: value
        });
        setErrors(newErrors);
    };

    return (
        <div className="card mt-4">
            <h1 className='schema-title'>Create Payment Schema</h1>
            <form>
                {success && <div className="alert alert-success">Schema created successfully!</div>}

                <div className="form-group-count">
                    <label htmlFor="count">Count Installments</label>
                    <input
                        type="text" // Използваме текстово поле за валидиране на тип
                        name="count"
                        className={`form-control no-spinner input-small ${errors.count ? 'is-invalid' : ''}`} // Добавяме стесняване чрез CSS клас
                        value={formData.count}
                        onChange={handleInputChange}
                        placeholder="Count"
                    />
                    {errors.count && <div className="invalid-feedback">{errors.count}</div>}
                </div>

                <button type="submit" className="btn btn-outline-primary mt-">
                    Create Schema                
                </button> 
            </form>
        </div>
    );
};

export default CreatePaymentSchema;
